function makeEqualAgain(n: Integer; a: array of Integer): Integer;var
  p, q, c, d, k: Integer;
begin
  k := n;
  p := 1;
  q := 0;
  for p := 1 to k - 1 do begin
    if a[p] <> a[p - 1] then
      Break;
  end;
  for q := k - 1 downto p - 1 do begin
    if a[q] <> a[p - 1] then
      Break;
  end;
  for d := k - 2 downto 0 do begin
    if a[d] <> a[d + 1] then
      Break;
  end;
  for c := 0 to d - 1 do begin
    if a[c] <> a[c + 1] then
      Break;
  end;
  if q - p + 1 < d - c + 1 then
    makeEqualAgain := q - p + 1
  else
    makeEqualAgain := d - c + 1;
end;

