{$ASSERTIONS ON}
uses
  SysUtils;
function countPermutations(n, k: int64; qq: array of int64): int64;var
  P, i, j, m, ans, c, kk: int64;
  q, jc, f: array[0..504] of int64;
  dp: array[0..504, 0..504] of int64;
begin
  P := 998244353;
  ans := 0;
  for i := 0 to n+2 do
  begin
    q[i] := 0;
    jc[i] := 0;
    f[i] := 0;
    for j := 0 to n+2 do
      dp[i][j] := 0;
  end;

  for i := 1 to n do
    q[i] := qq[i - 1];
  dp[0][0] := 1;
  f[0] := 1;
  jc[0] := 1;
  for i := 1 to n do
    jc[i] := (jc[i - 1] * i) mod P;
  for i := 1 to n do
  begin
    f[i] := jc[i];
    for j := 1 to i - 1 do
      f[i] := (f[i] + P - (f[j] * jc[i - j]) mod P) mod P;
  end;
  for i := 1 to n do
  begin
    for j := 0 to i - 1 do
      for kk := 1 to n do
        dp[i][kk] := (dp[i][kk] + (dp[j][kk - 1] * f[i - j]) mod P) mod P;
  end;
  m := 0;
  for i := 1 to n do
    if q[i] > q[i + 1] then
    begin
      m := i;
      break;
    end;
  if m = n then
  begin
    for i := k to n do
    begin
      ans := (ans + dp[n][i]) mod P;
    end
  end
  else
  begin
    for i := m + 1 to n do
    begin
      if ((i xor (m + 1) <> 0) and ((q[i - 1] > q[i]) or (q[i] < q[m]))) then
        break;
      c := k + i - n - 1;
      if c >= 0 then
        ans := (ans + (dp[m][c] * jc[i - m - 1]) mod P) mod P;
    end;
  end;
  countPermutations := ans;
end;

