function countPowerNumbers(n, k: integer): integer;var
  count, x, i, m: integer;
  t: int64;
  mp: array of boolean;
begin
  count := 0;
  x := 0;
  SetLength(mp, n + 1);
  i := 2;
  while i * i * i <= n do
  begin
    t := i * i;
    m := 2;
    while t <= n div i do
    begin
      t := t * i;
      Inc(m);
      if (m < k) or (mp[t]) then
        continue;

      if trunc(sqrt(t)) * trunc(sqrt(t)) = t then
        Inc(x);

      mp[t] := true;
      Inc(count);
    end;
    i := i + 1;
  end;

  if k = 1 then
  begin
    count := n;
  end
  else if k >= 3 then
  begin
    count := count + 1;
  end
  else
  begin
    count := count + trunc(sqrt(n)) - x;
  end;

  countPowerNumbers := count;
end;

