function maxModeSum(n: int64; const counts: array of int64): int64;var
  f: array[0..99999] of int64;
  mx, i: int64;
  ans : int64;
begin
  ans := 0;
  mx := 0;
  for i := 0 to n do f[i] := 0;

  for i := n downto 1 do
  begin
    while mx < counts[i - 1] do
    begin
      mx := mx + 1;
      f[mx] := f[mx - 1] + i;
    end;
    ans := ans + f[counts[i - 1]];
  end;
  maxModeSum := ans;
end;

