{$ASSERTIONS ON}

uses
  Math, SysUtils;
(*
    Calculates the value of the function for a given input.
      Parameters:
        - x (integer): Input value for the function.
      Returns:
        - A string with either the calculated function value rounded to 5 decimal places,
          or a notice that the input is not in the defined domain ("Not define").

      Function Definitions:
        - For 0 <= x < 10: y = cos(x + 3.0)
        - For 10 <= x < 20: y = (cos(x + 7.5))^2
        - For 20 <= x < 30: y = (cos(x + 4.0))^4
        >>> calculate_function_value(40)
        'Not define'
*)
function calculate_function_value(x: Integer): string;

var
  calculated_value: Double;
begin
  if (0 <= x) and (x < 10) then
  begin
    calculated_value := cos(x + 3.0);
    calculate_function_value := FormatFloat('0.00000', calculated_value);
  end
  else if (10 <= x) and (x < 20) then
  begin
    calculated_value := Power(cos(x + 7.5), 2.0);
    calculate_function_value := FormatFloat('0.00000', calculated_value);
  end
  else if (20 <= x) and (x < 30) then
  begin
    calculated_value := Power(cos(x + 4.0), 4.0);
    calculate_function_value := FormatFloat('0.00000', calculated_value);
  end
  else
  begin
    calculate_function_value := 'Not define';
  end;
end;

